import React, { useState, useEffect } from "react"
import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib"
import FolderIcon from '@mui/icons-material/Folder';
import AssignmentIcon from '@mui/icons-material/Assignment';
import PersonIcon from '@mui/icons-material/Person';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';


const MenuBar = (props: ComponentProps) => {
  const { work_duration, break_duration, workLabel = "Work", breakLabel = "Break", textColor = "skyblue", size = 250, strokeWidthInput = 1 } = props.args; // Default strokeWidthInput set to 5
 
  useEffect(() => {
    Streamlit.setFrameHeight()
  })

  return (
    <div style={{ 
      width: "100%", 
      height: "60px", // Set a height for the MenuBar
      display: "flex", // Add flex display
      justifyContent: "center", // Center the inner div
      alignItems: "center", // Center vertically if needed
      backgroundColor: "rgba(211, 211, 211, 0.04)", // Lighter grey and semi-transparent background
      position: "fixed", // Fix the position
      bottom: "0", // Attach to the bottom of the screen
      left: "0", // Ensure it spans the full width
      zIndex: 1000, // Ensure it appears on top
    }}>
      <div style={{ 
        display: "flex", 
        justifyContent: "space-around", // Dynamic spacing between icons
        alignItems: "center", 
        gap: "10px", 
        maxWidth: "350px", // Max width for the inner div
        width: "100%" // Allow it to take full width up to maxWidth
      }}>
        <div style={{ cursor: "pointer", display: "flex", alignItems: "center" }} title="File repository" onClick={() => Streamlit.setComponentValue({ selectedMenu: "file_repository" })}>
          <FolderIcon style={{ color: "#593773", margin: "15px", opacity: 0.4 }} /> {/* 50% transparent */}
        </div>
        <div style={{ cursor: "pointer", display: "flex", alignItems: "center" }} title="Project manager" onClick={() => Streamlit.setComponentValue({ selectedMenu: "project_manager" })}>
          <AssignmentIcon style={{ color: "#593773", margin: "15px", opacity: 0.4 }} /> {/* 50% transparent */}
        </div>
        <div style={{ cursor: "pointer", display: "flex", alignItems: "center" }} title="User" onClick={() => Streamlit.setComponentValue({ selectedMenu: "user" })}>
          <PersonIcon style={{ color: "#593773", margin: "15px", opacity: 0.4 }} /> {/* 50% transparent */}
        </div>
        <div style={{ cursor: "pointer", display: "flex", alignItems: "center" }} title="Logout" onClick={() => Streamlit.setComponentValue({ selectedMenu: "logout" })}>
          <ExitToAppIcon style={{ color: "#d9534f", margin: "15px", opacity: 0.4 }} /> {/* 50% transparent */}
        </div>
      </div>
    </div>
  );
}

export default withStreamlitConnection(MenuBar)
