import React, { useState, useEffect } from "react"
import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib"
import FolderIcon from '@mui/icons-material/Folder';
import AssignmentIcon from '@mui/icons-material/Assignment';
import PersonIcon from '@mui/icons-material/Person';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';


const MenuBar = (props: ComponentProps) => {
  const { work_duration, break_duration, workLabel = "Work", breakLabel = "Break", textColor = "skyblue", size = 250, strokeWidthInput = 1 } = props.args; // Default strokeWidthInput set to 5
 
  useEffect(() => {
    Streamlit.setFrameHeight()
  })

  return (
    <div style={{ width: size, height: size, position: "relative" }}>
      <div style={{ display: "flex", justifyContent: "space-around", alignItems: "center", gap: "10px" }}>
        <div style={{ cursor: "pointer", display: "flex", alignItems: "center" }} onClick={() => Streamlit.setComponentValue({ selectedMenu: "file_repository" })}>
          <FolderIcon style={{ color: "#593773", marginRight: "5px" }} />
        </div>
        <div style={{ cursor: "pointer", display: "flex", alignItems: "center" }} onClick={() => Streamlit.setComponentValue({ selectedMenu: "project_manager" })}>
          <AssignmentIcon style={{ color: "#593773", marginRight: "5px" }} />
        </div>
        <div style={{ cursor: "pointer", display: "flex", alignItems: "center" }} onClick={() => Streamlit.setComponentValue({ selectedMenu: "user" })}>
          <PersonIcon style={{ color: "#593773", marginRight: "5px" }} />
        </div>
        <div style={{ cursor: "pointer", display: "flex", alignItems: "center" }} onClick={() => Streamlit.setComponentValue({ selectedMenu: "logout" })}>
          <ExitToAppIcon style={{ color: "#d9534f", marginRight: "5px" }} />
        </div>
      </div>
    </div>
  );
}

export default withStreamlitConnection(MenuBar)
