import React, { useState, useEffect } from "react"
import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib"
import FolderOutlined from '@mui/icons-material/FolderOutlined';
import AssignmentOutlined from '@mui/icons-material/AssignmentOutlined';
import PersonOutline from '@mui/icons-material/PersonOutline';
import ExitToAppOutlined from '@mui/icons-material/ExitToAppOutlined';
import FolderIcon from '@mui/icons-material/Folder';
import AssignmentIcon from '@mui/icons-material/Assignment';
import PersonIcon from '@mui/icons-material/Person';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import Tooltip from '@mui/material/Tooltip';

const MenuBar = (props: ComponentProps) => {
  const { work_duration, break_duration, workLabel = "Work", breakLabel = "Break", textColor = "skyblue", size = 250, strokeWidthInput = 1 } = props.args; // Default strokeWidthInput set to 5
 
  useEffect(() => {
    Streamlit.setFrameHeight()
  })

  return (
    <div style={{ 
      width: "100%", 
      display: "flex", // Add flex display
      justifyContent: "center", // Center the inner div
      alignItems: "center", // Center vertically if needed
      backgroundColor: "rgba(211, 211, 211, 0.04)", // Lighter grey and semi-transparent background
    }}>
      <div style={{ 
        display: "flex", 
        justifyContent: "space-around", // Dynamic spacing between icons
        alignItems: "center", 
        gap: "10px", 
        maxWidth: "350px", // Max width for the inner div
        width: "100%", // Allow it to take full width up to maxWidth
        backgroundColor: "#f0f0f0", 
        boxShadow: "0 2px 5px rgba(0,0,0,0.1)", 
        height: "60px" 
      }}>
        <Tooltip 
          title="File repository" 
          arrow 
          PopperProps={{
            modifiers: [
              {
                name: 'offset',
                options: {
                  offset: [0, -53], // Adjust the second value to move the tooltip higher
                },
              },
            ],
          }}
        >
          <div style={{ cursor: "pointer", display: "flex", alignItems: "center", margin: "15px" }} onClick={() => Streamlit.setComponentValue({ selectedMenu: "file_repository" })}>
            <FolderOutlined fontSize="large" style={{ color: "#593773", opacity: 0.5 }} />
            <span style={{ marginLeft: "5px" }}>File repository</span>
          </div>
        </Tooltip>
        <Tooltip 
          title="Project manager" 
          arrow 
          PopperProps={{
            modifiers: [
              {
                name: 'offset',
                options: {
                  offset: [0, -53], // Adjust the second value to move the tooltip higher
                },
              },
            ],
          }}
        >
          <div style={{ cursor: "pointer", display: "flex", alignItems: "center", margin: "15px" }} onClick={() => Streamlit.setComponentValue({ selectedMenu: "project_manager" })}>
            <AssignmentOutlined fontSize="medium" style={{ color: "#593773", opacity: 0.85 }} />
          </div>
        </Tooltip>
        <Tooltip 
          title="User" 
          arrow 
          PopperProps={{
            modifiers: [
              {
                name: 'offset',
                options: {
                  offset: [0, -53], // Adjust the second value to move the tooltip higher
                },
              },
            ],
          }}
        >
          <div style={{ cursor: "pointer", display: "flex", alignItems: "center", margin: "15px" }} onClick={() => Streamlit.setComponentValue({ selectedMenu: "user" })}>
            <PersonOutline fontSize="medium" style={{ color: "#593773", opacity: 0.85 }} />
          </div>
        </Tooltip>
        <Tooltip 
          title="Logout" 
          arrow 
          PopperProps={{
            modifiers: [
              {
                name: 'offset',
                options: {
                  offset: [0, -53], // Adjust the second value to move the tooltip higher
                },
              },
            ],
          }}
        >
          <div style={{ cursor: "pointer", display: "flex", alignItems: "center", margin: "15px" }} onClick={() => Streamlit.setComponentValue({ selectedMenu: "logout" })}>
            <ExitToAppOutlined fontSize="medium" style={{ color: "#d9534f", opacity: 0.85 }} />
          </div>
        </Tooltip>
      </div>
    </div>
  );
}

export default withStreamlitConnection(MenuBar)
