import React, { useEffect } from "react";
import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib";
import FolderIcon from '@mui/icons-material/Folder';
import BuildIcon from '@mui/icons-material/Build';
import PersonIcon from '@mui/icons-material/Person';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';

const MenuBar = (props: ComponentProps) => {
  const { menuBarColor = "lightblue" } = props.args; // Default color set to light blue

  useEffect(() => {
    Streamlit.setFrameHeight();
  }, []);

  return (
    <div style={{
      width: "100%",
      position: "fixed",
      bottom: 0,
      left: 0,
      display: "flex",
      justifyContent: "space-around",
      alignItems: "center",
      backgroundColor: menuBarColor,
      padding: "10px 0",
      boxShadow: "0 -2px 5px rgba(0, 0, 0, 0.2)",
    }}>
      <div style={{ display: "flex", alignItems: "center", cursor: "pointer" }}>
        <FolderIcon style={{ border: "2px solid #ff5722", borderRadius: "50%", padding: "5px" }} /> {/* Border color for the icon */}
        <span style={{ marginLeft: "5px" }}>File Repository</span>
      </div>
      <div style={{ display: "flex", alignItems: "center", cursor: "pointer" }}>
        <BuildIcon />
        <span style={{ marginLeft: "5px" }}>Project Manager</span>
      </div>
      <div style={{ display: "flex", alignItems: "center", cursor: "pointer" }}>
        <PersonIcon />
        <span style={{ marginLeft: "5px" }}>User</span>
      </div>
      <div style={{ display: "flex", alignItems: "center", cursor: "pointer" }}>
        <ExitToAppIcon />
        <span style={{ marginLeft: "5px" }}>Logout</span>
      </div>
    </div>
  );
}

export default withStreamlitConnection(MenuBar);
