import React from "react";
import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib";

const MenuBar = (props: ComponentProps) => {
  const handleMenuClick = (menuItem: string) => {
    Streamlit.setComponentValue({ selectedMenu: menuItem });
  };

  return (
    <div style={{
      width: "100%",
      height: "80px",
      position: "fixed",
      bottom: 0,
      left: 0,
      display: "flex",
      justifyContent: "space-around",
      alignItems: "center",
      backgroundColor: "rgba(173, 216, 230, 0.9)",
      boxShadow: "0 -2px 10px rgba(0, 0, 0, 0.5)",
      zIndex: 1000,
    }}>
      <div style={{ cursor: "pointer" }} onClick={() => handleMenuClick("file_repository")}>
        <h2 style={{ color: "#593773" }}>File Repository</h2>
      </div>
      <div style={{ cursor: "pointer" }} onClick={() => handleMenuClick("project_manager")}>
        <h2 style={{ color: "#593773" }}>Project Manager</h2>
      </div>
      <div style={{ cursor: "pointer" }} onClick={() => handleMenuClick("user")}>
        <h2 style={{ color: "#593773" }}>User</h2>
      </div>
      <div style={{ cursor: "pointer" }} onClick={() => handleMenuClick("logout")}>
        <h2 style={{ color: "#d9534f" }}>Logout</h2>
      </div>
    </div>
  );
}

export default withStreamlitConnection(MenuBar);
