import React, { useEffect } from "react";
import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib";
import FolderIcon from '@mui/icons-material/Folder';
import BuildIcon from '@mui/icons-material/Build';
import PersonIcon from '@mui/icons-material/Person';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';

const MenuBar = (props: ComponentProps) => {
  const { menuBarColor = "rgba(173, 216, 230, 0.9)" } = props.args; // Default color set to light blue with slight transparency

  useEffect(() => {
    Streamlit.setFrameHeight();
  }, []);

  const handleMenuClick = (menuItem: string) => {
    // Send the clicked menu item back to Streamlit
    Streamlit.setComponentValue({ selectedMenu: menuItem });
  };

  return (
    <div style={{
      width: "100%",
      height: "80px", // Increased height for visibility
      position: "fixed",
      bottom: 0,
      left: 0,
      display: "flex",
      justifyContent: "space-around",
      alignItems: "center",
      backgroundColor: menuBarColor,
      padding: "10px 0",
      boxShadow: "0 -2px 10px rgba(0, 0, 0, 0.5)", // Increased shadow for better visibility
      borderTop: "5px solid #ff5722", // Added a border to make it stand out
      zIndex: 1000, // Ensure it appears above other elements
    }}>
      <div style={{ display: "flex", alignItems: "center", cursor: "pointer" }} onClick={() => handleMenuClick("file_repository")}>
        <FolderIcon style={{ fontSize: "48px", color: "#593773" }} />
        <span style={{ marginLeft: "5px", fontSize: "20px", color: "#593773" }}>File Repository</span>
      </div>
      <div style={{ display: "flex", alignItems: "center", cursor: "pointer" }} onClick={() => handleMenuClick("project_manager")}>
        <BuildIcon style={{ fontSize: "48px", color: "#593773" }} />
        <span style={{ marginLeft: "5px", fontSize: "20px", color: "#593773" }}>Project Manager</span>
      </div>
      <div style={{ display: "flex", alignItems: "center", cursor: "pointer" }} onClick={() => handleMenuClick("user")}>
        <PersonIcon style={{ fontSize: "48px", color: "#593773" }} />
        <span style={{ marginLeft: "5px", fontSize: "20px", color: "#593773" }}>User</span>
      </div>
      <div style={{ display: "flex", alignItems: "center", cursor: "pointer" }} onClick={() => handleMenuClick("logout")}>
        <ExitToAppIcon style={{ fontSize: "48px", color: "#d9534f" }} />
        <span style={{ marginLeft: "5px", fontSize: "20px", color: "#d9534f" }}>Logout</span>
      </div>
    </div>
  );
}

export default withStreamlitConnection(MenuBar);
