import React, { useEffect } from "react";
import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib";

const MenuBar = (props: ComponentProps) => {
  useEffect(() => {
    Streamlit.setFrameHeight();
  }, []);

  return (
    <div style={{
      width: "100%",
      height: "80px",
      position: "fixed",
      bottom: 0,
      left: 0,
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      backgroundColor: "rgba(173, 216, 230, 0.9)", // Light blue background
      boxShadow: "0 -2px 10px rgba(0, 0, 0, 0.5)",
      zIndex: 1000,
    }}>
      <h2 style={{ color: "#593773" }}>Menu Bar Loaded</h2> {/* Simple message */}
    </div>
  );
}

export default withStreamlitConnection(MenuBar);
